import { ClassNames } from '@emotion/react';
import { Typography, makeStyles } from '@material-ui/core';
import React, { useState } from 'react'
import Buildon from '../assets/Buildon2.gif'
import Recall from '../assets/Recall.gif'
import Store2 from '../assets/Store2.gif'
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepButton from '@mui/material/StepButton';
import StepContent from '@mui/material/StepContent';
import Button from '@mui/material/Button';

const withStyles = makeStyles(() => ({
    howToContentWrapper: {
        display: "flex",
        justifyContent: "space-evenly",
        alignItems: "center",
        padding: "10%"
    },
    gif: {
        width: "50%"
    },
    stepperContentWrapper: {
        display: "flex",
        justifyContent: "space-evenly",

    }
}));


const HowItWorks = () => {
    const classes = withStyles();

    const steps = [{
        label: 'Store',
        gif: Store2
    },
    {
        label: 'Build',
        gif: Buildon
    },
    {
        label: 'Recall',
        gif: Recall
    }];


    const [activeStep, setActiveStep] = useState(0);

    const handleStep = (step) => () => {
        setActiveStep(step);
    };

    return (
        <div>
            <Box sx={{ width: '100%' }}>
                <Stepper nonLinear activeStep={activeStep}>
                    {steps.map((step, index) => (
                        <Step key={step.label}>
                            <StepButton color="inherit" onClick={handleStep(index)}>
                                {step.label}
                            </StepButton>
                        </Step>
                    ))}
                </Stepper>
                <div className={classes.stepperContentWrapper}>
                    <div>Step description placeholder</div>
                    <img className={classes.gif} src={steps[activeStep].gif} />
                </div>
            </Box>
        </div>
    )
}

export default HowItWorks
