import React from 'react'
import { Button, Typography, makeStyles } from '@material-ui/core';
import EduceLogo from '../assets/educeLogo.png'
import MenuIcon from '@material-ui/icons/Menu';

const withStyles = makeStyles(() => ({
    navbarWrapper: {
        display: "flex",
        justifyContent: "space-between",
        background: "#AAF0C1",
        height: "75px",
        position: "fixed",
        top: 0,
        width: "100%"
    },
    navbarImage: {
        width: "200px",
        margin: "10px"
    },
    navbarMenuWrapper: {
        display: "flex"
    },
    menuItem: {
        margin: "auto 15px auto 15px !important",
    },
    loginButton: {
        backgroundColor: "black",
        color: "white",
        borderRadius: "35px",
        height: "35px",
        margin: "auto",
        marginRight: "10px"
    }

}));

const Nav = () => {

    const classes = withStyles();

    const [openDrawer, setOpenDrawer] = useState(false)

    const toggleDrawer = () => {
        setOpenDrawer(drawerOpen => !drawerOpen)
    }

    const handleClick = (id) => {
        let scrollToId = id
        setTimeout(() => scrollTo(scrollToId), 100)
    }
    return (
        <div className={classes.navbarWrapper}>
            <div>
                <img
                    className={classes.navbarImage}
                    src={EduceLogo}
                    alt="company logo"
                />
            </div>
            <div className={classes.navbarMenuWrapper}>
                <Typography className={classes.menuItem}>How it Works</Typography>
                <Typography className={classes.menuItem}>Contact</Typography>
                <Button className={classes.loginButton}>Login</Button>
            </div>
            <div className={classes.navBarHamburgerDrawerWrapper}>
                <MenuIcon
                    className={classes.hamburgerIcon}
                    onClick={toggleDrawer}
                />
                <Drawer
                    open={openDrawer}
                    onClose={toggleDrawer}
                    anchor="right"
                    className={classes.drawerRoot}
                >
                    <div
                        className={classes.list}
                        role="presentation"
                        onClick={toggleDrawer}
                        onKeyDown={toggleDrawer}
                    >
                        <List>
                            <ListItem
                                className={classes.drawerItem}
                                button
                                onClick={() => handleClick('#services')}
                            >
                                <ListItemText primary={"Services"} />
                            </ListItem>
                            <ListItem
                                className={classes.drawerItem}
                                button
                                // onClick={() => scrollTo('#about')}
                                onClick={() => handleClick("#about")}
                            >
                                <ListItemText primary={"About Us"} />
                            </ListItem>
                            <ListItem
                                className={classes.drawerItem}
                                button
                                onClick={() => handleClick('#contactForm')}
                            >
                                <ListItemText primary={"Contact Us"} />
                            </ListItem>
                            <ListItem
                                className={classes.drawerItem}
                                button
                                onClick={() => handleClick('#contactForm')}
                            >
                                <ListItemText primary={"Reviews"} />
                            </ListItem>
                        </List>
                    </div>
                </Drawer>
                <div className={classes.navBarButtonWrapper}>
                    <Button
                        className={classes.navButton}
                        onClick={() => scrollTo('#services')}

                    >
                        Services
                    </Button>
                    < Button
                        className={classes.navButton}
                        onClick={() => scrollTo('#about')}

                    >
                        About Us
                    </Button>
                    <Button
                        className={classes.navButton}
                        onClick={() => scrollTo('#contactForm')}

                    >
                        Contact Us
                    </Button>
                    <Button
                        className={classes.navButton}
                        onClick={() => scrollTo('#contactForm')}

                    >
                        Reviews
                    </Button>
                </div>
            </div>
        </div>
    )
}

export default Nav;
