import { ClassNames } from '@emotion/react';
import { Typography, makeStyles } from '@material-ui/core';
import React, {useEffect} from 'react'
import Buildon from '../assets/Buildon2.gif'
import Recall from '../assets/Recall.gif'
import Store2 from '../assets/Store2.gif'
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepButton from '@mui/material/StepButton';
import Button from '@mui/material/Button';

const withStyles = makeStyles(() => ({
    howToContentWrapper: {
        display: "flex",
        justifyContent: "space-evenly",
        alignItems: "center",
        padding: "10%"
    },
    gif: {
        width: "50%"
    }
}));


const HowItWorks = () => {
    const classes = withStyles();

    const [activeStep, setActiveStep] = React.useState(0);
    const [completed, setCompleted] = React.useState({});

    const totalSteps = () => {
        return steps.length;
    };

    const completedSteps = () => {
        return Object.keys(completed).length;
    };

    const isLastStep = () => {
        return activeStep === totalSteps() - 1;
    };

    const allStepsCompleted = () => {
        return completedSteps() === totalSteps();
    };

    const handleNext = () => {
        const newActiveStep =
            isLastStep() && !allStepsCompleted()
                ? // It's the last step, but not all steps have been completed,
                // find the first step that has been completed
                steps.findIndex((step, i) => !(i in completed))
                : activeStep + 1;
        setActiveStep(newActiveStep);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleStep = (step) => () => {
        setActiveStep(step);
    };

    const handleComplete = () => {
        const newCompleted = completed;
        newCompleted[activeStep] = true;
        setCompleted(newCompleted);
        handleNext();
    };

    const handleReset = () => {
        setActiveStep(0);
        setCompleted({});
    };

    return (
        <div>
            <div className={classes.howToContentWrapper}>
                <Typography>Store</Typography>
                <img className={classes.gif} src={Store2} />
            </div>
            <div className={classes.howToContentWrapper}>
                <Typography>Build</Typography>
                <img className={classes.gif} src={Buildon} />
            </div>
            <div className={classes.howToContentWrapper}>
                <Typography>Recall</Typography>
                <img className={classes.gif} src={Recall} />
            </div>
        </div>
    )
}

export default HowItWorks
